// ==UserScript==
// @name         DOM选择器获取辅助脚本_基于属性名与class
// @namespace    https://github.com/chenxuan353/tampermonkey/
// @version      1.0
// @description  获取当前鼠标指向元素尽可能唯一且精确的定位选择器，使用快捷键ctrl+x开启全局选择器获取，使用ctrl+b开启全局选择器标记，alt+b可以重新呼出全局选择器标记编辑框。需要手动添加允许的url。
// @author       chenxuan
// @match        https://twitter.com/*
// @grant        GM_addStyle
// @grant        GM_getResourceText
// @resource     layer https://cdn.bootcdn.net/ajax/libs/layer/3.1.1/theme/default/layer.min.css
// @require      https://cdn.bootcdn.net/ajax/libs/jquery/3.6.0/jquery.js
// @require      https://cdn.bootcdn.net/ajax/libs/layer/3.1.1/layer.min.js
// ==/UserScript==

(function() {
    'use strict';
    // 功能默认状态
    var mark_dom = ".mark_border_style";
    var mark_dom_switch = false;
    var script_switch = false;
    // 排除列表
    const EXCEPTCLASS = [
        "border_style","mark_border_style","r-zv2cs0"
    ];
    // Style 样式
    const Style = {};
    (function () {
        //注入layer的css
        const layerimgs = {
            "icon.png":
            "data:image/png;base64,",
            "icon-ext.png":
            "data:image/png;base64,",
            "loading-0.gif":
            "data:image/gif;base64,",
            "loading-1.gif":
            "data:image/gif;base64,",
            "loading-2.gif":
            "data:image/gif;base64,",
        };
        Style.addStyle = function (cssstr) {
            GM_addStyle(cssstr);
        };
        Style.load = function () {
            let layercss = GM_getResourceText("layer");
            for (let key in layerimgs) {
                layercss = layercss.replace(key, layerimgs[key]);
            }
            Style.addStyle(layercss);
            //注入layui的css
            //Style.addStyle(GM_getResourceText("layui"));
            //注入修复页面的css
            Style.addStyle(`
@font-face {
font-family: 'w-e-icon';
src: url(data:font/woff;base64,) format('truetype');
font-weight: normal;
font-style: normal;
}
.border_style{
outline:2px #0acc8a double;
z-index:999999;
}
.mark_border_style{
outline:2px #3425ce dashed;
z-index:999999;
}
.border_style:hover{
outline:3px #0acc8a double;
z-index:999999;
}
.mark_border_style:hover{
outline:3px #3425ce dashed;
z-index:999999;
}
`);
        };
        Style.load();
    })();
    var nowSelectDom = document.body;
    var combination = {
        ctrl:false,
        shift:false,
        alt:false,
    };
    var keys = [];
    function selectDom(){
        let uplevel = 5;// 向上查找的层级为5，向下单一查找的层级则不设限
        let nowDom = nowSelectDom;
        // 获取属性选择器
        let getAttrSelectStr = function(dom){
            const EXCLUDEATTRS = [
                "class","src","href","alt","style"
            ];
            let attrs = dom.attributes;
            let attrstr = "";
            let flag = true;
            for(let i = 0;i < attrs.length;i++){
                flag = true;
                for(let j in EXCLUDEATTRS){
                    if(EXCLUDEATTRS[j] == attrs[i].name){
                        flag = false;
                        break;
                    }
                }
                if(flag){
                    attrstr += "[" + attrs[i].name + "]";
                }

            }
            return attrstr;
        }
        let getSelects = function(dom,prefix = "",suffix = ""){
            let tag = dom.nodeName;
            let classList = dom.classList;
            let classListCounts = [];
            let flag;
            for(let i = 0;i < classList.length;i++){
                flag = false;
                for(let j = 0;j < EXCEPTCLASS.length;j++){
                    if(classList[i] == EXCEPTCLASS[j]){
                        flag = true;
                        break;
                    }
                }
                if(flag){
                    continue
                }
                let querystr = prefix + tag + "." + classList[i] + suffix;
                let queryresult = document.querySelectorAll(querystr);
                if(queryresult){
                    classListCounts.push({
                        count:queryresult.length,
                        querystr:querystr,
                        queryjsstr:'document.querySelectorAll("'+querystr + '")',
                        queryresult:queryresult,
                    });
                }
            }
            classListCounts.sort(function(a,b){
                return a.count - b.count;
            });
            return classListCounts;
        }
        let classLists = [];
        let prifixdom = nowDom;
        let prifix = "";
        let suffixdom = nowDom;
        let suffix = "";
        let classListCounts = getSelects(nowDom,prifix,suffix);
        let addClassLists = function(){
            try{
                classLists.push({
                    min:classListCounts[0].count,
                    minjsstr:classListCounts[0].queryjsstr,
                    minselect:classListCounts[0].querystr,
                    prifix:prifix,
                    suffix:suffix,
                    basicdom:nowDom,
                    mininfo:classListCounts[0],
                    counts:classListCounts,
                });
            }catch(e){
                console.log(e)
            }
        }
        addClassLists();
        // 向上查找
        while(uplevel > 0){
            uplevel--;
            let tp = nowDom.parentElement;
            if(tp == document.body){
                break;
            }
            if(tp.querySelectorAll(nowSelectDom.nodeName).length == 1){
                suffix = " " + nowSelectDom.nodeName + getAttrSelectStr(nowSelectDom);
            }else if(tp.querySelectorAll(nowDom.nodeName).length == 1){
                suffix += ">" + nowDom.nodeName + getAttrSelectStr(nowDom);
            }else{
                break;
            }
            nowDom = nowDom.parentElement;
            classListCounts = getSelects(nowDom,"",suffix);
            addClassLists();
        }
        prifixdom = nowDom;
        nowDom = nowSelectDom;
        // 向下查找
        while(true){
            uplevel--;
            prifix = nowDom.nodeName + getAttrSelectStr(nowDom) + ">" + prifix;
            if(nowDom.children.length != 1){
                break;
            }
            nowDom = nowDom.children[0];
            classListCounts = getSelects(nowDom,"","");
            addClassLists();
        }
        suffixdom = nowDom;
        classLists.sort(function(a,b){
            return a.min - b.min;
        });
        if(classLists.length > 0){
            console.log("-----定位数据-----");
            console.log("最低计数：",classLists[0].min);
            console.log("最低计数JS：",classLists[0].minjsstr);
            console.log("共检查元素：",classLists.length);
            console.log("前缀：",prifix);
            console.log("后缀：",suffix);
            console.log("最简详情\n",classLists[0]);
            console.log("\n最外层dom\n",prifixdom,"\n最内层dom\n",suffixdom,"\n所有检索信息\n",classLists);
            if(!mark_dom_switch){
                mark_dom = classLists[0].minselect;
            }
        }else{
            console.log(classLists);
        }
    }
    document.addEventListener("keydown", function(e){
        combination.ctrl = e.ctrlKey;
        combination.shift = e.shiftKey;
        combination.alt = e.altKey;
        keys[e.which] = true;
    }, false);
    let openMarkWindowIndex = null;
    document.addEventListener("keyup", function(e){
        if(script_switch && combination.ctrl && !e.ctrlKey && !openMarkWindowIndex){
            selectDom();
        }
        if(e.which == 88 && combination.ctrl){
            script_switch = !script_switch;
            if(script_switch){
                layer.msg("按住ctrl键获取元素最简选择器并输出到控制台");
            }else{
                layer.msg("结构获取已关闭");
                $(".border_style").removeClass("border_style");
            }
        }
        if(e.which == 66 && combination.alt){
            if(mark_dom_switch && !openMarkWindowIndex){
                openMarkWindowIndex = layer.prompt({
                    title: '输入您要标记的选择器',
                    formType: 2,
                    value:mark_dom,
                    end:function(){
                        openMarkWindowIndex = null;
                    }
                }, function(text, index){
                    layer.close(index);
                    openMarkWindowIndex = null;
                    $(".mark_border_style").removeClass("mark_border_style");
                    mark_dom = text;
                });
            }
        }
        if(e.which == 88 && combination.alt || e.which == 66 && combination.ctrl){
            if(!openMarkWindowIndex){
                mark_dom_switch = !mark_dom_switch;
                if(mark_dom_switch){
                    openMarkWindowIndex = layer.prompt({
                        title: '输入您要标记的选择器',
                        formType: 2,
                        value:mark_dom,
                        end:function(){
                            openMarkWindowIndex = null;
                        }
                    }, function(text, index){
                        layer.close(index);
                        openMarkWindowIndex = null;
                        $(".mark_border_style").removeClass("mark_border_style");
                        mark_dom = text;
                    });
                }else{
                    layer.msg("自动标记已关闭");
                    $(".mark_border_style").removeClass("mark_border_style");
                }
            }
        }
        combination.ctrl = e.ctrlKey;
        combination.shift = e.shiftKey;
        combination.alt = e.altKey;
        keys[e.which] = false;
    }, false);

    function autoMark(){
        if(mark_dom_switch){
            $(mark_dom).addClass("mark_border_style");
        }
        setTimeout(autoMark,500);
    }
    autoMark();
    $(document).mouseover(function(e){
        if(script_switch){
            $(nowSelectDom).removeClass("border_style");
        }

        nowSelectDom = e.target;

        if(script_switch){
            $(nowSelectDom).addClass("border_style");
            if(combination.ctrl && !openMarkWindowIndex){
                selectDom();
            }
        }
    });


    // Your code here...
})();